import React, { Component, Fragment } from "react";
import Helmet from "react-helmet";

class QuizSummary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      score: 0,
      numberOfQuestions: 0,
      numberOfAnsweredQuestions: 0,
      correctAnswers: 0,
      wrongAnswers: 0,
      hintsUsed: 0,
    };
  }
  componentDidMount() {
    const { state } = this.props.location;
    this.setState({
      score: (state.score / state.numberOfQuestions) * 100,
      numberOfQuestions: state.numberOfQuestions,
      numberOfAnsweredQuestions: state.numberOfAnsweredQuestions,
      correctAnswers: state.correctAnswers,
      wrongAnswers: state.wrongAnswers,
      usedHints: state.hintsUsed,
    });
  }
  render() {
    const { state, score } = this.props.location;
    let stats, remark;
    if (score <= 30) {
      remark = "Trebate još malo vježbanja";
    } else if (score > 30 && score <= 50) {
      remark = "Više sreće drugi put";
    } else if (score > 50 && score <= 70) {
      remark = "Možes ti još bolje!";
    } else if (score > 70 && remark <= 89) {
      remark = "Vrlo dobro!";
    } else {
      remark = "Genijalac!!!";
    }
    if (state !== undefined) {
      stats = (
        <Fragment>
          <div>
            <span className="mdi mdi-check-circle-outline succes-icon"></span>
          </div>
          <h1>Kviz je gotov!</h1>
          <div className="container">
            <h4>{remark}</h4>
            <h2> Vaš rezultat {this.state.score.toFixed(0)}&#37;</h2>
            <span className="stat left">
              Ukupan broj pitanja:
              <span className="right">{this.state.numberOfQuestions}</span>
            </span>

            <span className="stat left">
              Ukupan broj pitanja:
              <span className="right">{this.state.numberOfQuestions}</span>
            </span>
            <br></br>

            <span className="stat left">
              Ukupan broj pitanja:
              <span className="right">{this.state.numberOfQuestions}</span>
            </span>
            <br></br>

            <span className="stat left">
              Ukupan broj pitanja:
              <span className="right">{this.state.numberOfQuestions}</span>
            </span>
            <br></br>

            <span className="stat left">
              Ukupan broj pitanja:
              <span className="right">{this.state.numberOfQuestions}</span>
            </span>
          </div>
        </Fragment>
      );
    } else {
      stats = <h1>No stats available!</h1>;
    }
    return (
      <Fragment>
        <Helmet>
          <title>Sažetak</title>
        </Helmet>
        {stats}
      </Fragment>
    );
  }
}

export default QuizSummary;
